# --- Stripe Webhook View ---
class StripeWebhookView(APIView):
    permission_classes = [permissions.AllowAny]

    @csrf_exempt
    def post(self, request, *args, **kwargs):
        payload = request.body
        sig_header = request.META.get('HTTP_STRIPE_SIGNATURE')
        endpoint_secret = settings.STRIPE_WEBHOOK_SECRET

        try:
            if not stripe:
                return Response({"error": "Stripe not available"}, status=status.HTTP_501_NOT_IMPLEMENTED)
            event = stripe.Webhook.construct_event(
                payload, sig_header, endpoint_secret
            )
        except ValueError as e:
            # Invalid payload
            return Response(status=status.HTTP_400_BAD_REQUEST)
        except Exception as e:  # Handle both Stripe errors and missing stripe module
            # Invalid signature
            return Response(status=status.HTTP_400_BAD_REQUEST)

        # Audit the event
        audit_entry, created = PaymentEventAudit.objects.get_or_create(
            provider="stripe",
            event_id=event.id,
            defaults={
                'event_type': event.type,
                'payload': json.loads(payload),
            }
        )
        if not created:
            # Event already processed, idempotent response
            return Response(status=status.HTTP_200_OK)

        # Handle the event
        if event.type == 'charge.succeeded':
            charge = event.data.object
            user_email = charge.billing_details.email
            user, _ = User.objects.get_or_create(email=user_email, defaults={'username': user_email})

            payment, p_created = Payment.objects.update_or_create(
                stripe_charge_id=charge.id,
                defaults={
                    'user': user,
                    'amount': charge.amount / 100.0,
                    'currency': charge.currency,
                    'status': charge.status,
                    'description': charge.description,
                }
            )
            audit_entry.related_payment = payment
        
        audit_entry.status = "processed"
        audit_entry.processed_at = timezone.now()
        audit_entry.save()

        return Response(status=status.HTTP_200_OK)


# --- Google OAuth Views ---

class GoogleAuthInitView(APIView):
    """Инициирует Google OAuth процесс - возвращает URL для авторизации"""
    permission_classes = (permissions.AllowAny,)
    
    def get(self, request):
        # Google OAuth настройки
        client_id = getattr(settings, 'GOOGLE_OAUTH_CLIENT_ID', 'test-client-id-12345')
        redirect_uri = request.build_absolute_uri('/api/auth/google/callback/')
        scope = 'openid email profile'
        
        # Генерируем state для безопасности  
        state = ''.join(secrets.choice(string.ascii_letters + string.digits) for i in range(32))
        request.session['google_oauth_state'] = state
        
        # Строим Google OAuth URL
        params = {
            'client_id': client_id,
            'redirect_uri': redirect_uri,
            'scope': scope,
            'response_type': 'code',
            'state': state,
            'access_type': 'offline',
            'prompt': 'consent'
        }
        
        auth_url = f"https://accounts.google.com/o/oauth2/auth?{urlencode(params)}"
        
        return Response({
            'auth_url': auth_url,
            'state': state
        })


class GoogleAuthCallbackView(APIView):
    """Обрабатывает callback от Google OAuth и создает/авторизует пользователя"""
    permission_classes = (permissions.AllowAny,)
    
    def get(self, request):
        # Получаем код авторизации от Google
        code = request.GET.get('code')
        state = request.GET.get('state')
        error = request.GET.get('error')
        
        if error:
            # Редирект с ошибкой
            return HttpResponseRedirect(f"{settings.FRONTEND_URL}/auth#error={error}")
        
        if not code or not state:
            return HttpResponseRedirect(f"{settings.FRONTEND_URL}/auth#error=missing_parameters")
        
        # Проверяем state для безопасности
        session_state = request.session.get('google_oauth_state')
        if not session_state or session_state != state:
            return HttpResponseRedirect(f"{settings.FRONTEND_URL}/auth#error=invalid_state")
        
        try:
            # В MVP используем mock данные для Google OAuth
            # В продакшене здесь будет реальный обмен кода на токены с Google API
            
            # Mock пользователь от Google
            google_user_data = {
                'email': 'user@gmail.com',
                'given_name': 'Test',
                'family_name': 'User',
                'sub': '1234567890'  # Google user ID
            }
            
            # Создаем или получаем пользователя
            user, created = User.objects.get_or_create(
                email=google_user_data['email'],
                defaults={
                    'username': google_user_data['email'],
                    'first_name': google_user_data.get('given_name', ''),
                    'last_name': google_user_data.get('family_name', ''),
                    'is_active': True,
                }
            )
            
            # Генерируем JWT токены
            refresh = RefreshToken.for_user(user)
            access_token = refresh.access_token
            
            # Очищаем session state
            if 'google_oauth_state' in request.session:
                del request.session['google_oauth_state']
            
            # Редирект с токенами в hash для frontend
            redirect_url = f"{settings.FRONTEND_URL}/auth#access={access_token}&refresh={refresh}"
            return HttpResponseRedirect(redirect_url)
            
        except Exception as e:
            return HttpResponseRedirect(f"{settings.FRONTEND_URL}/auth#error=oauth_failed")
